    <script>document.addEventListener("DOMContentLoaded", intersectionObserver);
        function intersectionObserver() {
            let options = {
                //root: document.querySelector("body"),
                rootMargin: "0px 0px 0px 0px",
                threshold: 0,
            };

            let FadeInDownOnScroll = (entries, observer) => {
                entries.forEach((entry) => {
                    const intersecting = entry.isIntersecting
                    if (intersecting) {
                        entry.target.classList.add('is-visible');
                        observer.unobserve(entry.target);
                    }
                });
            };

            let observer = new IntersectionObserver(FadeInDownOnScroll, options);

            let target = document.querySelectorAll('.observed');
            target.forEach(instance => {
                if (instance) {
                    observer.observe(instance);
                }
            })
        }
    </script>
